// script.js

// API Setup
const API_KEY = '69b6e28cc1cdeb23c5b61f798baa70be';
const API_URL = 'https://api.openweathermap.org/data/2.5/weather';

// DOM Elements
const locationEl = document.getElementById('location');
const temperatureEl = document.getElementById('temperature');
const conditionEl = document.getElementById('condition');
const humidityEl = document.getElementById('humidity');
const windEl = document.getElementById('wind');
const weatherIconEl = document.getElementById('weather-icon');

// Fetch Weather Data
async function fetchWeather(city = 'Chicago') {
    try {
        const response = await fetch(`${API_URL}?q=${city}&units=imperial&appid=${API_KEY}`);
        const data = await response.json();

        // Update DOM with API data
        locationEl.textContent = `${data.name}, ${data.sys.country}`;
        temperatureEl.textContent = `${Math.round(data.main.temp)}°F`;
        conditionEl.textContent = data.weather[0].description;
        humidityEl.textContent = `${data.main.humidity}%`;
        windEl.textContent = `${Math.round(data.wind.speed)} mph`;

        // Update Weather Icon
        const iconCode = data.weather[0].icon;
        weatherIconEl.src = `https://openweathermap.org/img/wn/${iconCode}@2x.png`;
    } catch (error) {
        locationEl.textContent = 'Error fetching weather!';
        console.error(error);
    }
}

// Initial Fetch
fetchWeather();
